riot.tag2("ftl-error-message",'<div show="{show}" ref="erros" class="container" style="margin-top:20px"> <div each="{message, idx in this.opts.messages}" no-reorder class="alert alert-{message.type} col-md-10" style="margin-top:20px" role="alert"> {message.msg} <ul each="{item, index in message.itens}"><li>{item.msg}</li></ul> <virtual if="{message.total > 0}"><ul>E mais {message.total} registro(s).</ul></virtual> </div> </div>',"","",(function(e){var s=this;s.on("mount",()=>{s.show=null!=s.opts.messages,s.update()}),this.hideMessage=function(e){s.show=!1,e.preventDefault()}.bind(this)}));